import pokeInfo from './baseUrl'

pokeInfo
const getAllPokeData = () =>  baseURL.get('/pokemon/?limit=1279')

const getPokeInfo = (id) =>  baseURL.get(`/pokemon/${id}/`)
const getPokeSpecies = (id) => baseURL.get(`/pokemon-species/${id}`)

const getEvoChain = (idUrl) => baseURL.get(`/evolution-chain/${idUrl}`)

function DisplayPokeInfo(){
    const { loading, error, data } = useQuery(getPokeInfo);
    if (loading) return <p>Loading...</p>;
    if (error) return <p>Error : {error.message}</p>;
    return data?.pokemon_v2_pokemon?.map(({id ,name, order, base_experience, height, pokemon_v2_pokemonstats, pokemon_v2_pokemontypes}) => (
      <div key={id}>
        {/* <img width="400" height="250" alt="location-reference" src={`${photo}`} />
        <br /> */}
        <h1>{name}</h1>
        <h3>Pokedex Index: {order}</h3>
        <h3>Base Experience: {base_experience}</h3>
        <h3>Height: {height}</h3>
        <h3>Stats:
          <ul>
            {pokemon_v2_pokemonstats.map(item=>(
               <><li>Stat Name: {item?.pokemon_v2_stat.name}</li>
               <li>Base Stat: {item?.base_stat}</li></>
              ))}
          </ul>
        </h3>
  
        <h3>Type:
          <ul>
            {pokemon_v2_pokemontypes.map(type => (
              <li>{type?.pokemon_v2_type.name}</li>
            ))}
          </ul>
        </h3>
      </div>))}


export { getAllPokeData, getPokeInfo, getPokeSpecies, getEvoChain}
