import React, { useEffect, useState } from 'react';
// import { getAllPokeData } from '../../apis/apis';
import { Link } from "react-router-dom";
import pokeLogo from '../../assets/pokeLogo';
import { useQuery, gql } from '@apollo/client';
// import {PokeApi} from '../../apis/baseUrl';

import CatchingPokemonIcon from '@mui/icons-material/CatchingPokemon';
import Grid from '@mui/material/Grid';
import Table from '@mui/material/Table';
import TableHead from '@mui/material/TableHead';
import TableBody from '@mui/material/TableBody';
import TableCell from '@mui/material/TableCell';
import TableContainer from '@mui/material/TableContainer';
import TableFooter from '@mui/material/TableFooter';
import TablePagination from '@mui/material/TablePagination';
import TableRow from '@mui/material/TableRow';
import Paper from '@mui/material/Paper';

const PokeApi = gql`
      query queryPokeInfo {
        pokemon_v2_pokemon {
          base_experience
          height
          id
          name
          pokemon_species_id
          order
          pokemon_v2_pokemonstats {
            base_stat
            pokemon_v2_stat {
              name
            }
          }
          pokemon_v2_pokemontypes {
            pokemon_v2_type {
              name
            }
          }
        }
      }
    `

const pokeInfo = new ApolloClient({
  uri: 'https://beta.pokeapi.co/graphql/v1beta',
  cache: new InMemoryCache(),
});


const HomeScreen = () => {
	//const [allPokeData, setAllPokeData] = useState([]);
	// useEffect(() => {
	// 	getAllPokeData().then((res) => {
	// 		setAllPokeData(res.data.results)
	// 	}).catch((er) => {
	// 		console.log(er)
	// 	})
	// }, [])
	// const getUrlPoke = (url) => {			// 	let idUrl = url.split('/')[6]
	//	return idUrl
	// }

	const [page, setPage] = useState(0);
	const [rowsPerPage, setRowsPerPage] = useState(10);

	const PokeID = (obj) => { 
		return (<Link to={`/pokeDetails/${obj}`}><CatchingPokemonIcon /></Link>)
	}
	const handleChangePage = (event, newPage) => {setPage(newPage);};
				
	const handleChangeRowsPerPage = (event) => {
		setRowsPerPage(parseInt(event.target.value, 10));
		setPage(0);
	};
	interface PokeData{
	name: string,
	}

	const { loading, error, data } = useQuery(PokeApi);
		if (loading) return <p>Loading...</p>;
		if (error) return <p>Error : {error.message}</p>;
	return data?.pokemon_v2_pokemon?.map(({id ,name, order, base_experience, height, pokemon_v2_pokemonstats, pokemon_v2_pokemontypes}) => (
		<Grid container sx={{
			display:'flex',
			flexDirection:'row',
			justifyContent:'center',
			backgroundColor: 'red'}}>
		<Grid item
				xs = {12} 
				sx = {{
				height: '100vh',
				gap: '1em',
				display: 'flex',
				flexDirection: 'column',
				justifyContent: 'center',
				alignItems: 'center',
				}}>
				<img src={pokeLogo} alt='pokeLogo' height='300vh' />
		</Grid>
		<Grid item-
				xs={6}
				sx={{
					alignItems:'center',
					alignContent:'center',
					justifyContent:'center'
				}}>
				<TableContainer component={Paper}>
					<Table sx={{ 
						minWidth: 500, 
						backgroundColor: 'beige' 		
					}} aria-label="POKEDEX">
						<TableHead>
							<TableRow>
								<TableCell align="center">Position</TableCell>
								<TableCell align="center">Pokemon Name</TableCell>
								<TableCell align="center">Go!</TableCell>
							</TableRow>
						</TableHead>
						<TableBody>
							{(name.slice(page * rowsPerPage, page * rowsPerPage + rowsPerPage)
							).map((name) => 
								<TableRow key={name}>
									<TableCell component="th" scope="row" style={{ width: 160 }} align="center">
										{/* ARMAR UNA LISTA NUMERICA QUE TOME EN CUENTA TODOS LOS POKEMONES  */}
										NÂ° {order}
									</TableCell>
									<TableCell style={{ width: 160 }} align="center">
										{name}
									</TableCell>
									<TableCell style={{ width: 160 }} align="center">
										{PokeID(id)}
									</TableCell>
								</TableRow>
							)}
						</TableBody>
						<TableFooter sx={{
							alignContent:'center',
						}}>
							<TableRow>
								<TablePagination
									rowsPerPageOptions={[10, 20, 30, { label: 'All', value: -1 }]}
									count={name.length}
									rowsPerPage={rowsPerPage}
									page={page}
									onPageChange={handleChangePage}
									onRowsPerPageChange={handleChangeRowsPerPage}
								/>
							</TableRow>
						</TableFooter>
					</Table>
				</TableContainer>
			</Grid>
		</Grid>
	)
	);
}
export default HomeScreen;
