import React, {useState} from "react";
import {gql, useLazyQuery} from "@apollo/client";

const GET_Poke_Name = gql `
query GetPokeName($name: String!){
    pokemon_v2_pokemon(filter:  {
        name: $name}) {
            results {
                base_experience
                height
                weight
                id
                name
                pokemon_species_id
                order
                pokemon_v2_pokemonstats {
                    base_stat
                    pokemon_v2_stat {
                        name
                    }
                }
                pokemon_v2_pokemontypes {
                    pokemon_v2_type {
                        name
                    }
                }
            }
        }
    }
}`

export default function Search(){
    const [name, setName] = useState("")

    const [getName, {loading, error, data, called}] = useLazyQuery(GET_Poke_Name, {
        variables: {
            name,
        }
    })
    
    console.log({
        called,
        loading,
        error,
        data,
    });

    return <></>
}
