import React, { useEffect, useState } from 'react';
import { useNavigate, useParams } from "react-router-dom";
import { useQuery } from '@apollo/client';
import {PokeApi} from '../apis/baseUrl';
//import { getPokeInfo, getPokeSpecies, getEvoChain } from "../apis/apis";

import Link from '@mui/material/Link';
import Card from '@mui/material/Card';
import CardContent from '@mui/material/CardContent';
import CardMedia from '@mui/material/CardMedia';
import Typography from '@mui/material/Typography';
import Grid from '@mui/material/Grid';
import List from '@mui/material/List';
import ListItemButton from '@mui/material/ListItemButton';
import ListItemText from '@mui/material/ListItemText';
import Collapse from '@mui/material/Collapse';
import ExpandLess from '@mui/icons-material/ExpandLess';
import ExpandMore from '@mui/icons-material/ExpandMore';

const PokeDetails = () => {
    const params = useParams()
    // const [pokeDetail, setPokeDatail] = useState<PokeData>();
    // const [pokeType, setPokeType] = useState<PokeData[]>([]);
    // const [pokeAbility, setPokeAbility] = useState<PokeData[]>([]);
    // const [pokeMoves, setPokeMoves] = useState<PokeData[]>([]);
    // const [PokeUrlEvo, setPokeUrlEvo] = useState()
    // const [pokeFirstEvo, setPokeFirstEvo] = useState<PokeData>()
    // const [pokeSecondEvo, setPokeSecondEvo] = useState<PokeData[]>([])
    // // solucion ante arrays anidados 
    // const [pokeThirdEvo, setPokeThirdEvo] = useState<Array <PokeData[]>>([])
    // const [pokeFirstEvoName, setPokeFirstEvoName] = useState()
    const [open, setOpen] = React.useState(true);
    const [open2, setOpen2] = React.useState(true);
    const [open3, setOpen3] = React.useState(true);
    const [open4, setOpen4] = React.useState(true);
    const [open5, setOpen5] = React.useState(true);
    const [open6, setOpen6] = React.useState(true);
    const [open7, setOpen7] = React.useState(true);
    const navigate = useNavigate();

//    useEffect(() => {
// //        getPokeInfo(params.id)
// //            .then((res) => {
//                 setPokeDatail(res.data)
//                 setPokeType(res.data.types)
//                 setPokeAbility(res.data.abilities)
//                 setPokeMoves(res.data.moves)
//             })
//         getPokeSpecies(params.id)
//             .then((res) => {
//                 setPokeUrlEvo(res.data.evolution_chain.url)
//                 let idUrl = res.data.evolution_chain.url.split('/')[6]
//                 getEvoChain(parseInt(idUrl))
//                     .then((res) => {
//                         setPokeFirstEvo(res.data.chain.species.url);
//                         setPokeSecondEvo(res.data.chain.evolves_to.map(item => item.species));
//                         setPokeThirdEvo(res.data.chain.evolves_to.map(item => item.evolves_to.map(item => item.species)));
//                         setPokeFirstEvoName(res.data.chain.species.name);
//                     })
//             })
//             .catch((er) => {
//                 console.log(er)
//             })
//     }, [, params.id])
    const handleClick = () => {
        setOpen(!open);
    };
    const handleClick2 = () => {
        setOpen2(!open2);
    };
    const handleClick3 = () => {
        setOpen3(!open3);
    };
    const handleClick4 = () => {
        setOpen4(!open4);
    };
    const handleClick5 = () => {
        setOpen5(!open5);
    };
    const handleClick6 = () => {
        setOpen6(!open6);
    };
    const handleClick7 = () => {
        setOpen7(!open7);
    };
    // const firstMoves = pokeMoves.slice(0, 10)
    const getIdPoke = (url) => {
        let idUrl = url.split('/')[6]
        return idUrl
    }
    const getidPoke2 = (item) => {
        let evo2 = item.split('/')[6]
        return evo2
    }
    const getidPoke3 = (item) => {
        let evo3 = item.split('/')[6]
        return evo3
    }
    interface PokeData{   
		name: string,
        sprites: any,
        id: number,
        base_experience: number,
        height: number,
        weight: number,
        type: Row,
        ability:Row,
        move: Row,
        url:string

	}
    // se puede usar interfaces anidadas, y a la vez valores que pueden existir o no en otras variables ej: strong?: string,
    interface Row{
        name:string, 
    }

    const { loading, error, data } = useQuery(PokeApi)
		if (loading) return <p>Loading...</p>;
		if (error) return <p>Error : {error.message}</p>;
		console.log(data)
		// data?.pokemon_v2_pokemon?.map(({id ,name, order, base_experience, height, pokemon_v2_pokemonstats, pokemon_v2_pokemontypes}) => (
    return (
        <Grid container
        sx={{
			display:'flex',
			flexDirection:'row',
			justifyContent:'center',
			backgroundColor: 'red'
		}}>
            <Grid item 
                xs={6} 
                sx={{
                display: 'flex',
                alignItems:'center',
                alignContent:'center',
                justifyContent:'center',
                textAlign: 'center'}}>

                <Card sx={{
                    bgcolor: 'beige',
                    maxWidth:500,
                    width:'100%'}}>
                    {/* <CardMedia
                        sx={{   
                            height: '300px',
                            backgroundPosition: 'center',
                            backgroundSize: 'contain',
                            backgroundImage: `url(${pokeDetail?.sprites.other.dream_world.front_default})`,
                            margin: 'auto',
                            width:400,
                            maxWidth: '100%',
                            maxHeight: '100%',
                        }}
                        image={pokeDetail?.sprites.other.dream_world.front_default?pokeDetail?.sprites.other.dream_world.front_default:pokeDetail?.sprites.front_default}
                        title={pokeDetail?.name}/> */}
                    <CardContent sx={{textAlign: 'center', alignItems: 'center'}}>
                        <Typography gutterBottom variant="h3" component="div">
                            {data?.pokemon_v2_pokemon[0]?.name.charAt(0).toUpperCase() + data?.pokemon_v2_pokemon[0]?.name.slice(1)}
                        </Typography>
                        <Grid color="text.secondary">
                            <List component="nav" aria-labelledby="nested-list-subheader">
                                <ListItemText>
                                    Pokedex Index: {data?.pokemon_v2_pokemon?.order}
                                </ListItemText>
                                <ListItemText>
                                    Base Experience: {data?.pokemon_v2_pokemon?.base_experience}
                                </ListItemText>
                                <ListItemText>
                                    Height: {data?.pokemon_v2_pokemon?.height}
                                </ListItemText>
                                <ListItemText>
                                    Weight: {data?.pokemon_v2_pokemon?.weight}
                                </ListItemText>
                                <ListItemText onClick={handleClick7}>
                                    Type: {open7 ? <ExpandLess /> : <ExpandMore />}
                                </ListItemText>
                                <Collapse in={open7} timeout="auto" unmountOnExit>
                                    <List component="div" disablePadding>
                                        {/* <ListItemText>
                                            {pokeType?.map((element,i) => (<ListItemText key={i}>{element.type.name}</ListItemText>))}
                                        </ListItemText> */}
                                    </List>
                                </Collapse>
                                <ListItemText onClick={handleClick6}>
                                    Abilities: {open6 ? <ExpandLess /> : <ExpandMore />}
                                </ListItemText>
                                <Collapse in={open6} timeout="auto" unmountOnExit>
                                    <List component="div" disablePadding>
                                        {/* <ListItemText>
                                            {pokeAbility?.map((element, i) => (<ListItemText key={i}>{element.ability.name}</ListItemText>))}
                                        </ListItemText> */}
                                    </List>
                                </Collapse>
                                <ListItemText onClick={handleClick}>
                                    First Moves: {open ? <ExpandLess /> : <ExpandMore />}
                                </ListItemText>
                                <Collapse in={open} timeout="auto" unmountOnExit>
                                    <List component="div" disablePadding>
