import React from 'react';
import { useNavigate, useParams } from "react-router-dom";
import { useQuery, gql } from '@apollo/client';

//import CardMedia from '@mui/material/CardMedia';
import Link from '@mui/material/Link';
import Card from '@mui/material/Card';
import CardContent from '@mui/material/CardContent';
import Typography from '@mui/material/Typography';
import Grid from '@mui/material/Grid';
import List from '@mui/material/List';
import ListItemButton from '@mui/material/ListItemButton';
import ListItemText from '@mui/material/ListItemText';
import Collapse from '@mui/material/Collapse';
import ExpandLess from '@mui/icons-material/ExpandLess';
import ExpandMore from '@mui/icons-material/ExpandMore';

const PokeApiID = gql`
      query QueryPokeInfo ($name:String!) {
        pokemon_v2_pokemon( where: { name: { _eq: $name } }){
          base_experience
          height
          weight
          id
          name
          pokemon_species_id
          order
          pokemon_v2_pokemonstats {
            base_stat
            pokemon_v2_stat {
              name
            }
          }
          pokemon_v2_pokemontypes {
            pokemon_v2_type {
              name
            }
          }
        }
        TotalPoke: pokemon_v2_pokemon_aggregate {
          aggregate {
            count
          }
        }
      }
    `
const PokeDetails = () => {
    const {name} = useParams();
    const { loading, error, data } = useQuery(PokeApiID, {variables: {name}})


    const [open, setOpen] = React.useState(true);
    const [open2, setOpen2] = React.useState(true);
    const [open3, setOpen3] = React.useState(true);
    const [open4, setOpen4] = React.useState(true);
    const [open5, setOpen5] = React.useState(true);
    const [open6, setOpen6] = React.useState(true);
    const [open7, setOpen7] = React.useState(true);
    const navigate = useNavigate();

    const handleClick = () => {
        setOpen(!open);
    };
    const handleClick2 = () => {
        setOpen2(!open2);
    };
    const handleClick3 = () => {
        setOpen3(!open3);
    };
    const handleClick4 = () => {
        setOpen4(!open4);
    };
    const handleClick5 = () => {
        setOpen5(!open5);
    };
    const handleClick6 = () => {
        setOpen6(!open6);
    };
    const handleClick7 = () => {
        setOpen7(!open7);
    };

    if (loading) return <p>Loading...</p>;
	if (error) return <p>Error : {error.message}</p>;
	console.log(data);
	// data?.pokemon_v2_pokemon?.map(({id ,name, order, base_experience, height, pokemon_v2_pokemonstats, pokemon_v2_pokemontypes}) => (
    return (data?.pokemon_v2_pokemon?.map(item=>{
        <Grid container
        sx={{
			display:'flex',
			flexDirection:'row',
			justifyContent:'center',
			backgroundColor: 'red'
		}}>
            <Grid item 
                xs={6} 
                sx={{
                display: 'flex',
                alignItems:'center',
                alignContent:'center',
                justifyContent:'center',
                textAlign: 'center'}}>

                <Card sx={{
                    bgcolor: 'beige',
                    maxWidth:500,
                    width:'100%'}}>
                    {/* <CardMedia
                        sx={{   
                            height: '300px',
                            backgroundPosition: 'center',
                            backgroundSize: 'contain',
                            backgroundImage: `url(${pokeDetail?.sprites.other.dream_world.front_default})`,
                            margin: 'auto',
                            width:400,
                            maxWidth: '100%',
                            maxHeight: '100%',
                        }}
                        image={pokeDetail?.sprites.other.dream_world.front_default?pokeDetail?.sprites.other.dream_world.front_default:pokeDetail?.sprites.front_default}
                        title={pokeDetail?.name}/> */}
                    <CardContent sx={{textAlign: 'center', alignItems: 'center'}}>
                        <Typography gutterBottom variant="h3" component="div">
                            {item?.name.charAt().toUpperCase() + item?.name.slice()}
                        </Typography>
                        <Grid color="text.secondary">
                            <List component="nav" aria-labelledby="nested-list-subheader">
                                <ListItemText>
                                    Pokedex Index: {item?.order}
                                </ListItemText>
                                <ListItemText>
                                    Base Experience: {item?.base_experience}
                                </ListItemText>
                                <ListItemText>
                                    Height: {item?.height}
                                </ListItemText>
                                <ListItemText>
                                    Weight: {item?.weight}
                                </ListItemText>
                                <ListItemText onClick={handleClick7}>
                                    Type: {open7 ? <ExpandLess /> : <ExpandMore />}
                                </ListItemText>
                                <Collapse in={open7} timeout="auto" unmountOnExit>
                                    <List component="div" disablePadding>
                                        {/* <ListItemText>
                                            {pokeType?.map((element,i) => (<ListItemText key={i}>{element.type.name}</ListItemText>))}
                                        </ListItemText> */}
                                    </List>
                                </Collapse>
                                <ListItemText onClick={handleClick6}>
                                    Abilities: {open6 ? <ExpandLess /> : <ExpandMore />}
                                </ListItemText>
                                <Collapse in={open6} timeout="auto" unmountOnExit>
                                    <List component="div" disablePadding>
                                        {/* <ListItemText>
                                            {pokeAbility?.map((element, i) => (<ListItemText key={i}>{element.ability.name}</ListItemText>))}
                                        </ListItemText> */}
                                    </List>
                                </Collapse>
                                <ListItemText onClick={handleClick}>
                                    First Moves: {open ? <ExpandLess /> : <ExpandMore />}
                                </ListItemText>
                                <Collapse in={open} timeout="auto" unmountOnExit>
                                    <List component="div" disablePadding>
                                        {/* <ListItemText>
                                            {firstMoves?.map((element, i) => (<ListItemText key={i}>{element.move.name}</ListItemText>))}
                                        </ListItemText> */}
                                    </List>
                                </Collapse>
                                <ListItemText onClick={handleClick2}>
                                    Evolution Chain: {open2 ? <ExpandLess /> : <ExpandMore />}
                                </ListItemText>
                                <Collapse in={open2} timeout="auto" unmountOnExit>
                                    <List  disablePadding>
                                        <ListItemButton onClick={handleClick5} sx={{justifyContent:'center'}}>
                                            1st Evo Chain: {open5 ? <ExpandLess /> : <ExpandMore />}
                                        </ListItemButton>
                                        {/* <Collapse in={open5} timeout="auto" unmountOnExit>
                                        <ListItemButton sx={{ pl: 4, justifyContent:'center' }} onClick={() => navigate(`/pokeDetails/${getIdPoke(pokeFirstEvo)}`)}>
                                                {pokeFirstEvoName}
                                        </ListItemButton>
                                        </Collapse> */}
                                        <ListItemButton onClick={handleClick3} sx={{justifyContent:'center'}}>
                                            2nd Evo Chain : {open3 ? <ExpandLess /> : <ExpandMore />}
                                        </ListItemButton>
                                        {/* <Collapse in={open3} timeout="auto" unmountOnExit>
                                            { pokeSecondEvo?.length > 1 ?
                                                pokeSecondEvo?.map(item => <ListItemButton sx={{ pl: 4, justifyContent:'center' }} onClick={() => navigate(`/pokeDetails/${getidPoke2(item.url)}`)}>
                                                    {item.name}</ListItemButton>) :
                                                pokeSecondEvo?.map(item => <ListItemButton sx={{ pl: 4, justifyContent:'center' }} onClick={() => navigate(`/pokeDetails/${getidPoke2(item.url)}`)}>
                                                    {item.name}</ListItemButton>)}
                                        </Collapse> */}
                                        <ListItemButton onClick={handleClick4} sx={{justifyContent:'center'}}>
                                            3rd Evo Chain : {open4 ? <ExpandLess /> : <ExpandMore />}
                                        </ListItemButton>
                                        {/* <Collapse in={open4} timeout="auto" unmountOnExit>
                                            {pokeThirdEvo !== undefined && pokeThirdEvo[0]?.length > 0 ? pokeThirdEvo?.map(item => item.map(item => <ListItemButton sx={{ pl: 4, justifyContent:'center'}} onClick={() => navigate(`/pokeDetails/${getidPoke3(item.url)}`)}>
                                                {item.name}</ListItemButton>)) :
                                                pokeThirdEvo?.map(item => item.map(item => <ListItemButton sx={{ pl: 4, justifyContent:'center' }} onClick={() => navigate(`/pokeDetails/${getidPoke3(item.url)}`)}>
                                                    {item.name}</ListItemButton>))}
                                        </Collapse> */}
                                    </List>
                                </Collapse>
                            </List>
                        </Grid>
                        <Link underline="hover" color="black" href="/">
                            Home
                        </Link>
                    </CardContent>
                </Card>
            </Grid>
        </Grid>
        })
    )
    
};
export default PokeDetails
