import './App.css';
import { useQuery, gql } from '@apollo/client';
import {Routes, Route} from 'react-router-dom'
import PokeDetails from './pages/pokeDetails.tsx'
import HomeScreen from './pages/home/HomeScreen.tsx';


// function App() {
//   return (
//   <Routes>
//     <Route path='/' element={<HomeScreen/>}/>
//     <Route path='/pokeDetails/:id' element={<PokeDetails/>}/>
//   </Routes>
//   );

//   // pagina 404, re-direct
// }

function App() {
  return (
    <div>
      <h2>My first Apollo app ðŸš€</h2>
      <DisplayPokeInfo/>
    </div>
  );
}
const getPokeInfo = gql`
  query queryPokeInfo {
    {
      pokemon_v2_pokemon {
        base_experience
        height
        id
        name
        pokemon_species_id
        order
        pokemon_v2_pokemonstats {
          base_stat
          pokemon_v2_stat {
            name 
          }
        }
        pokemon_v2_pokemontypes {
          pokemon_v2_type {
            name
          }
        }
      }
    }
  }
`;

function DisplayPokeInfo(){
  const { loading, error, data } = useQuery(getPokeInfo);
  if (loading) return <p>Loading...</p>;
  if (error) return <p>Error : {error.message}</p>;
  return data?.pokemon_v2_pokemon?.map(({id ,name, order, base_experience, height, pokemon_v2_pokemonstats, pokemon_v2_pokemontypes}) => (
    <div key={id}>
      {/* <img width="400" height="250" alt="location-reference" src={`${photo}`} />
      <br /> */}
      <h1>{item?.name}</h1>
      <h3>Pokedex Index: {item?.order}</h3>
      <h3>Base Experience: {item?.base_experience}</h3>
      <h3>Height: {item?.height}</h3>
      <h3>Stats:
        <ul>
          {item?.pokemon_v2_pokemonstats.map(item=>(
             <><li>Stat Name: {item?.pokemon_v2_stat.name}</li><li>Base Stat: {item?.base_stat}</li></>
            ))}
        </ul>
      </h3>

      <h3>Type:
        <ul>
          {item?.pokemon_v2_pokemontypes.map(type => (
            <li>{type?.pokemon_v2_type.name}</li>
          ))}
        </ul>
      </h3>
      {/* <h3>Type:<ul>{item?.pokemon_v2_pokemontypes.map(
             item=>{
             <><li>{item?.pokemon_v2_type.name}</li></>
            })}</ul></h3> */}
    </div>))}

export default App;
