import React, { useState, ChangeEvent } from 'react';
import pokeLogo from '../../assets/pokeLogo';
import { useQuery, gql, useLazyQuery } from '@apollo/client';
import { Link, MemoryRouter, Route, Routes, useLocation } from 'react-router-dom';

import InputBase from '@mui/material/InputBase';
import Divider from '@mui/material/Divider';
import IconButton from '@mui/material/IconButton';
import SearchIcon from '@mui/icons-material/Search';
import CatchingPokemonIcon from '@mui/icons-material/CatchingPokemon';
import Grid from '@mui/material/Grid';
import Table from '@mui/material/Table';
import TableHead from '@mui/material/TableHead';
import TableBody from '@mui/material/TableBody';
import TableCell from '@mui/material/TableCell';
import TableContainer from '@mui/material/TableContainer';
import TableFooter from '@mui/material/TableFooter';
import TableRow from '@mui/material/TableRow';
import Paper from '@mui/material/Paper';
import Pagination from '@mui/material/Pagination';
import PaginationItem from '@mui/material/PaginationItem';
import Box from '@mui/material/Box';
import InputLabel from '@mui/material/InputLabel';
import MenuItem from '@mui/material/MenuItem';
import FormControl from '@mui/material/FormControl';
import Select, { SelectChangeEvent } from '@mui/material/Select';

const PokeApi = gql(/* GraphQL */`
      query QueryPokeInfo{
        pokemon_v2_pokemon
		{
          id
          name
        }
		TotalPoke: pokemon_v2_pokemon_aggregate {
			aggregate {
			  count
			}
		  }
      }`)
const GET_Poke_Ubi = gql`
	query GetPokeName($name: String!){
		pokemon_v2_pokemon( where:
			{ name: { _ilike: $name } })
			{
			id
			name
		}
	}`

const HomeScreen = () => {
	const location = useLocation();
	const query = new URLSearchParams(location.search);

	//const [page, setPage] = useState(0);
	const page = parseInt(query.get('page') || '0', 15);
	//const offset = (page - 1)* 12;
	
	const [rowsPerPage, setRowsPerPage] = useState(15);
	const { loading, error, data } = useQuery(PokeApi);
	const [name, setName] = useState<string>("")
	const [getResult, { data: dataB }] = useLazyQuery(GET_Poke_Ubi, {
		variables: {
			name: `%${name}%`,
		}
	})

	// const handleChangePage = (event, newPage) => {setPage(newPage);};
	// const handleChangeRowsPerPage = (event) => {
	// 	setRowsPerPage(parseInt(event.target.value, 10));
	// 	setPage(0);
	// };

	if (loading) return <p>Loading...</p>;
	if (error) return <p>Error : {error.message}</p>;
	const PokeID = (obj) => {
		return (<Link to={`/pokeDetails/${obj}`}><CatchingPokemonIcon /></Link>)
	}
	const onSearchChange = (e: ChangeEvent<HTMLInputElement>) => {
		setName(e.target.value);
	  };

	interface PokeData {
		name: string,
		order: number,
		id: number
	}
	console.log(dataB)
	// data?.pokemon_v2_pokemon?.map(({id ,name, order, base_experience, height, pokemon_v2_pokemonstats, pokemon_v2_pokemontypes}) => (
	return (<Grid container sx={{
		display: 'flex',
		flexDirection: 'row',
		justifyContent: 'center',
		backgroundColor: 'red'
	}}>
		<Grid item
			xs={12}
			sx={{
				height: '100vh',
				gap: '1em',
				display: 'flex',
				flexDirection: 'column',
				justifyContent: 'center',
				alignItems: 'center',
			}}>
			<img src={pokeLogo} alt='pokeLogo' height='300vh' />
		</Grid>
		<Grid item-
			xs={6}
			sx={{
				alignItems: 'center',
				alignContent: 'center',
				justifyContent: 'center'
			}}>
			<TableContainer component={Paper}>
				<Paper
					component="form"
					sx={{ p: '2px 4px', display: 'flex', alignItems: 'center', width: '100%', backgroundColor: 'gold' }}>
					<InputBase
						sx={{ ml: 1, flex: 1 }}
						placeholder="Pokemon Search"
						inputProps={{ 'aria-label': 'search google maps' }}
						value={name}
						onChange={onSearchChange}
						onKeyPress={(e) => {
							if (e.key === "Enter") {
								getResult();
							}
						}}
					/>
					<Divider sx={{ height: 28, m: 0.5 }} orientation="vertical" />
					<IconButton type="button" sx={{ p: '10px' }} aria-label="search" onClick={() => getResult()}>
						<SearchIcon />
					</IconButton>
				</Paper>
				<Table sx={{
					minWidth: 500,
					backgroundColor: 'beige'
				}} aria-label="POKEDEX">
					<TableHead>
						<TableRow>
							<TableCell align="center">Position</TableCell>
							<TableCell align="center">Pokemon Name</TableCell>
							<TableCell align="center">Go!</TableCell>
						</TableRow>
					</TableHead>
					<TableBody>
						{dataB && dataB?.pokemon_v2_pokemon?.length > 0
							? (dataB?.pokemon_v2_pokemon?.slice(page * rowsPerPage, page * rowsPerPage + rowsPerPage)
							).map((item: PokeData) =>
								<TableRow key={item.name}>
									<TableCell component="th" scope="row" style={{ width: 160 }} align="center">
										N° {item.id}
									</TableCell>
									<TableCell style={{ width: 160 }} align="center">
										{item.name}
									</TableCell>
									<TableCell style={{ width: 160 }} align="center">
										{PokeID(item?.name)
										}
									</TableCell>
								</TableRow>
							)
							: (data?.pokemon_v2_pokemon?.slice(page * rowsPerPage, page * rowsPerPage + rowsPerPage)
							).map((item: PokeData) =>
								<TableRow key={item.name}>
									<TableCell component="th" scope="row" style={{ width: 160 }} align="center">
										N° {item.id}
									</TableCell>
									<TableCell style={{ width: 160 }} align="center">
										{item.name}
									</TableCell>
									<TableCell style={{ width: 160 }} align="center">
										{PokeID(item?.name)
										}
									</TableCell>
								</TableRow>
							)}
					</TableBody>
					<TableFooter sx={{
						alignContent: 'center',
						width: '100%'
					}}>
						<TableRow>
						{/* <TablePagination
									rowsPerPageOptions={[10, 20, 30, { label: 'All', value: -1 }]}
									count={allPokeData.length}
									rowsPerPage={rowsPerPage}
									page={page}
									onPageChange={handleChangePage}
									onRowsPerPageChange={handleChangeRowsPerPage}
								/> */}
							<TableCell colSpan={3} align="center" sx={{ width: '100%', backgroundColor: 'gold' } } >	
							<Pagination
								sx={{ p: '2px 4px',
								justifyContent: 'center', 
								display: 'flex',
								width: '100%', 
								backgroundColor: 'gold' }}
								page={page}
								count={(Math.round(data.TotalPoke.aggregate.count/10))-1}
								renderItem={(item) => (
									<PaginationItem
										component={Link}
										to={`/${item.page === 0 ? '' : `?page=${item.page}`}`}
										{...item}
									/>
								)}
							/>
							</TableCell>
						</TableRow>
					</TableFooter>
				</Table>
			</TableContainer>
		</Grid>
	</Grid>
	)
}
export default HomeScreen;
